package com.tuputech.api.model;public class Options {	private String[] tags;	private String[] CIDs;	private String uid = null;	public Options() {	}	public Options(String[] tags, String uid, String[] CIDs) {		this.tags = tags;		this.uid = uid;		this.CIDs = CIDs;	}	public String[] getTags() {		return tags;	}	public void setTags(String[] tags) {		this.tags = tags;	}	public String getUid() {		return uid;	}	public void setUid(String uid) {		this.uid = uid;	}	public String[] getCIDs() {		return CIDs;	}	public void setCID(String[] CIDs) {		this.CIDs = CIDs;	}}